mapper.select:
SELECT b.user_id AS userId, b.user_name AS UserName FROM user_role a JOIN user b ON a.user_id = b.user_id WHERE 1=1
AND a.role_name = #{roleName}
AND b.user_id = #{userId}

SELECT distinct t.id, t.name, t.description , t.time_id as timeId, t.parent_id as parentId, (SELECT COUNT(1) FROM flow_team WHERE lft > t.lft AND rht < t.rht) AS childCount, tr.role_id as roleId, t.lft, t.rht FROM team t
left
JOIN (SELECT distinct t.id FROM (SELECT DISTINCT t1.lft,t1.rht FROM team t1 JOIN team_role tr ON tr.team_id = t1.id AND tr.is_parent = 1 AND tr.role_id = #{roleId}) t1 JOIN team t ON t.lft < t1.lft AND t.rht > t1.rht) t1 ON t.parent_id = t1.id LEFT JOIN team_role tr ON tr.team_id = t.id AND tr.role_name = #{roleName}
WHERE t.id >0
AND t.id = #{id}
AND t.parent_id = #{parentId}
ORDER BY lft ASC
