mapper.select:
SELECT distinct t.uuid AS uuid, t.name, t.description , t.time_id as timeId, t.parent_id as parentId, (SELECT COUNT(1) FROM team WHERE lft > t.lft AND rht < t.rht) AS childCount, tr.role_name as roleName, t.lft, t.rht FROM team t LEFT JOIN team_role tr ON tr.team_uuid = t.uuid WHERE 1=1
AND tr.role_name = #{roleName}
AND t.parent_id = #{parentId}
ORDER BY lft ASC

SELECT uuid, name, lft, rht, parent_id as parentId, is_handle_childtask AS isHandleChildtask FROM
team WHERE uuid =
#{value}

SELECT uuid, NAME, a.description,is_handle_childtask as isHandleChildtask FROM team a 
WHERE
a.uuid = #{value}

mapper.update:
update team
set
lft = #{lft},
rht = #{rht}
where
uuid = #{uuid}
