mapper.select:
SELECT COUNT(1) FROM user a where 1=1
and a.user_id = #{userId}
and (a.user_id like CONCAT('%', #{userName}, '%') OR a.user_name like CONCAT('%', #{userName}, '%'))

mapper.update:
update user_info
set
company = #{company},
dept = #{dept},
position = #{position}
where user_id = #{userId}

mapper.delete:
delete from
user_team
where user_id = #{userId}

	and team_uuid = #{teamUuid}
