mapper.select:
SELECT a.id, a.name, a.icon AS icon, a.parent_id AS parentId, e.name AS parentName, a.url, a.sort, a.is_active as isActive, c.role_name as roleName, a.description, a.module, a.is_auto AS isAuto, a.open_mode openMode , b.id AS childId, b.name AS childName, b.icon AS childIcon, b.parent_id AS childParentId, b.url AS childUrl, b.sort AS childSort, b.is_active AS childIsActive, d.role_name AS childRoleName, b.description AS childDescription, b.module AS childModule, b.open_mode AS childOpenMode, b.is_auto AS childIsAuto FROM menu a LEFT JOIN menu b ON b.`parent_id` = a.`id` LEFT JOIN menu e ON a.`parent_id` = e.`id` LEFT JOIN menu_role c ON a.id = c.menu_id LEFT JOIN menu_role d ON b.id = d.menu_id
WHERE 1=1
AND a.parent_id = #{parentId}
AND a.parent_id != 0
and a.is_active = #{isActive}
AND a.module = #{module}
AND a.id = #{id}
AND (c.role_name IN
#{item}
or c.role_name is null)
AND (d.role_name IN
#{item}
or d.role_name is null)
ORDER BY b.parent_id, b.sort
